// Extract and validate search query
const webhookData = $input.first().json;
let query = 'rrr'; // default fallback

// Try multiple ways to extract query
if (webhookData.query && webhookData.query.query) {
  query = webhookData.query.query;
} else if (webhookData.body && webhookData.body.query) {
  query = webhookData.body.query;
} else if (webhookData.query && typeof webhookData.query === 'string') {
  query = webhookData.query;
} else if (webhookData.searchQuery) {
  query = webhookData.searchQuery;
}

// Clean and validate query
query = String(query).trim();
if (!query || query === 'undefined' || query === 'null') {
  query = 'rrr';
}

// Construct target URL for 5movierulz.villas
const targetUrl = `https://www.5movierulz.villas/search_movies?s=${encodeURIComponent(query)}`;

console.log('Search query:', query);
console.log('Target URL:', targetUrl);

return {
  searchQuery: query,
  targetUrl: targetUrl,
  timestamp: new Date().toISOString()
};

// Parse movie results from 5movierulz.villas HTML
const httpData = $input.first().json;
const searchQuery = $('Prepare Search Data').first().json.searchQuery;

let htmlContent = '';
if (httpData.body) {
  htmlContent = httpData.body;
} else if (httpData.data) {
  htmlContent = httpData.data;
} else if (typeof httpData === 'string') {
  htmlContent = httpData;
}

console.log('HTML content length:', htmlContent.length);
console.log('Search query for parsing:', searchQuery);

const movies = [];

// Check if we got blocked or redirected
if (htmlContent.includes('Access Denied') || 
    htmlContent.includes('Cloudflare') || 
    htmlContent.includes('Just a moment') ||
    htmlContent.length < 1000) {
  console.log('Possible blocking detected');
  return {
    error: 'Site may be blocking requests',
    htmlLength: htmlContent.length,
    searchQuery: searchQuery
  };
}

// Parse using regex patterns for 5movierulz.villas structure
// Look for movie items with various possible class names
const moviePatterns = [
  /<div[^>]*class="[^"]*ml-item[^"]*"[^>]*>([\s\S]*?)<\/div>/gi,
  /<article[^>]*class="[^"]*item[^"]*"[^>]*>([\s\S]*?)<\/article>/gi,
  /<div[^>]*class="[^"]*movie[^"]*"[^>]*>([\s\S]*?)<\/div>/gi
];

const titleRegex = /<h[1-6][^>]*>\s*<a[^>]*>([^<]+)<\/a>\s*<\/h[1-6]>/i;
const linkRegex = /<a[^>]*href="([^"]+)"[^>]*>/i;
const imageRegex = /<img[^>]*src="([^"]+)"[^>]*>/i;
const yearRegex = /<span[^>]*class="[^"]*year[^"]*"[^>]*>([^<]+)<\/span>/i;
const ratingRegex = /<span[^>]*class="[^"]*rating[^"]*"[^>]*>([^<]+)<\/span>/i;
const genreRegex = /<span[^>]*class="[^"]*genre[^"]*"[^>]*>([^<]+)<\/span>/i;

// Try each pattern
for (const pattern of moviePatterns) {
  let match;
  while ((match = pattern.exec(htmlContent)) !== null) {
    const itemHtml = match[1];

    const titleMatch = titleRegex.exec(itemHtml);
    const linkMatch = linkRegex.exec(itemHtml);
    const imageMatch = imageRegex.exec(itemHtml);
    const yearMatch = yearRegex.exec(itemHtml);
    const ratingMatch = ratingRegex.exec(itemHtml);
    const genreMatch = genreRegex.exec(itemHtml);

    if (titleMatch && linkMatch) {
      const title = titleMatch[1].trim();
      let url = linkMatch[1].trim();

      // Ensure absolute URL
      if (url.startsWith('/')) {
        url = 'https://www.5movierulz.villas' + url;
      }

      let image = imageMatch ? imageMatch[1].trim() : '';
      if (image && image.startsWith('/')) {
        image = 'https://www.5movierulz.villas' + image;
      }

      const movie = {
        title: title,
        url: url,
        image: image || 'https://via.placeholder.com/300x450?text=No+Image',
        year: yearMatch ? yearMatch[1].trim() : 'Unknown',
        rating: ratingMatch ? ratingMatch[1].trim() : 'N/A',
        genre: genreMatch ? genreMatch[1].trim() : 'Unknown',
        source: '5movierulz.villas',
        searchQuery: searchQuery,
        scrapedAt: new Date().toISOString()
      };

      movies.push(movie);
    }
  }

  // If we found movies with this pattern, break
  if (movies.length > 0) {
    break;
  }
}

// Alternative parsing if main method fails - look for any links with movie-like text
if (movies.length === 0) {
  console.log('Trying alternative parsing methods...');

  const linkPattern = /<a[^>]*href="([^"]+)"[^>]*>([^<]+)<\/a>/gi;
  let linkMatch;

  while ((linkMatch = linkPattern.exec(htmlContent)) !== null) {
    const url = linkMatch[1].trim();
    const title = linkMatch[2].trim();

    // Filter for movie-like URLs and titles
    if (url.includes('movie') || url.includes('watch') || 
        title.toLowerCase().includes(searchQuery.toLowerCase())) {

      let fullUrl = url;
      if (url.startsWith('/')) {
        fullUrl = 'https://www.5movierulz.villas' + url;
      }

      movies.push({
        title: title,
        url: fullUrl,
        image: 'https://via.placeholder.com/300x450?text=Movie',
        year: 'Unknown',
        rating: 'N/A',
        genre: 'Unknown',
        source: '5movierulz.villas',
        searchQuery: searchQuery,
        scrapedAt: new Date().toISOString()
      });

      // Limit to reasonable number
      if (movies.length >= 10) {
        break;
      }
    }
  }
}

console.log(`Found ${movies.length} movies for query: ${searchQuery}`);

// Return each movie as separate item for n8n processing
return movies;

// Aggregate all movies and format final response
const movies = $input.all();
const searchQuery = movies[0]?.json?.searchQuery || 'rrr';

// Remove duplicates based on title
const uniqueMovies = [];
const seenTitles = new Set();

for (const movie of movies) {
  const title = movie.json.title.toLowerCase().trim();
  if (!seenTitles.has(title)) {
    seenTitles.add(title);
    uniqueMovies.push(movie.json);
  }
}

// Sort by relevance (title similarity to search query)
const sortedMovies = uniqueMovies.sort((a, b) => {
  const aRelevance = a.title.toLowerCase().includes(searchQuery.toLowerCase()) ? 1 : 0;
  const bRelevance = b.title.toLowerCase().includes(searchQuery.toLowerCase()) ? 1 : 0;
  return bRelevance - aRelevance;
});

const finalResult = {
  searchQuery: searchQuery,
  totalResults: sortedMovies.length,
  source: '5movierulz.villas',
  scrapedAt: new Date().toISOString(),
  scrapeMethod: 'http-request-advanced',
  status: 'success',
  movies: sortedMovies
};

console.log(`Final result: ${sortedMovies.length} unique movies for "${searchQuery}"`);

return finalResult;

// Return success response for webhook
const inputData = $input.first().json;

return {
  success: true,
  message: 'Movie scraping completed successfully',
  searchQuery: inputData.searchQuery || 'unknown',
  totalResults: inputData.totalResults || 0,
  timestamp: new Date().toISOString(),
  webhookResponse: {
    status: 'completed',
    data: inputData
  }
};
